const initialState = { page: 'notes', notes: [] };

function reducer(state = initialState, action) {
  const { type, payload } = action;
  switch(type) {
    case 'page-changed':
      state.page = payload.value;
      break;
    case 'notes-updated':
      state.notes = payload.notes;
      break;
    default:
      break;
  }
  console.log(state, 'reducer');
  return state;
}

export default reducer;
