import store from '../Store/index';
const { ipcRenderer } = window.require('electron');

export const createNote = async(note) => {
  console.log('inside action ...');
  // const newNote = ipcRenderer.sendSync('save-note', note);
  ipcRenderer.on('save-note-reply', (event, note) => {
    console.log('newNote->>>>>>>>>>>>>', note);
    store.dispatch({ type: 'create-note', payload: { note } });
  })
  ipcRenderer.send('note', note);

  // console.log('newNote->>>>>>>>>>>>>', newNote);
  // store.dispatch({ type: 'create-note', payload: { note: newNote } });
};

// "build": "npm run build-react && npm run build-electron",
