import { Sequelize } from 'sequelize';
import mysql2 from 'mysql2';

const sequelize = new Sequelize(
  'aura',
  'davo',
  'root1234!',
  {
    host: 'localhost',
    port: 3306,
    dialect: 'mysql',
    dialectModule: mysql2,
    pool: {
      max: 30,
      min: 0,
      acquire: 60000,
      idle: 60000,
    },
    define: {
      charset: 'utf8mb4',
      collate: 'utf8mb4_general_ci',
      timestamps: true,
    },  
  },
);

export default sequelize;
