import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import './components.css';

let notes = [
  {
    title: 'note 1',
    text: 'Lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white',
  },
  {
    title: 'note 2',
    text: 'Lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white',
  },
  {
    title: 'note 3',
    text: 'Lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white',
  },
  {
    title: 'note 4',
    text: 'Lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white',
  },
  {
    title: 'note 5',
    text: 'Lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white',
  }
];

class Notes extends React.Component {
  render() {
    let rowQuantity = Math.ceil(notes.length / 3);
    let rows = [];
    for(let i = 0; i < rowQuantity; i++) {
      rows.push(
        notes.filter((note, index) => {
          if(index < i + 3) {
            return <Col className='note-col'>1 of 2</Col>;
          }
        })
      );
    }
    return (
      <Container>
        {
          rowQuantity.map(() => {
            return (
              <Row>
                {}
              </Row>
            );
          })
        }
        <Row>
          <Col className='note-col'>1 of 2</Col>
          <Col className='note-col'>2 of 2</Col>
        </Row>
        <Row>
          <Col className='note-col'>1 of 3</Col>
          <Col className='note-col'>2 of 3</Col>
          <Col className='note-col'>3 of 3</Col>
        </Row>
      </Container>
    );
  }
}

export default Notes;
