'use strict';

const LRU = require('lru-cache');

const parserCache = new LRU({
  max: 15000
});

function keyFromFields(type, fields, options, config) {
  let res = "".concat(type) + "/".concat(typeof options.nestTables) + "/".concat(options.nestTables) + "/".concat(options.rowsAsArray) + "/".concat(options.supportBigNumbers || config.supportBigNumbers) + "/".concat(options.bigNumberStrings || config.bigNumberStrings) + "/".concat(typeof options.typeCast) + "/".concat(options.timezone || config.timezone) + "/".concat(options.decimalNumbers) + "/".concat(options.dateStrings);

  for (let i = 0; i < fields.length; ++i) {
    const field = fields[i];
    res += "/".concat(field.name, ":").concat(field.columnType, ":").concat(field.flags, ":").concat(field.characterSet);

    if (options.nestTables) {
      res += ":".concat(field.table);
    }
  }

  return res;
}

function getParser(type, fields, options, config, compiler) {
  const key = keyFromFields(type, fields, options, config);
  let parser = parserCache.get(key);

  if (parser) {
    return parser;
  }

  parser = compiler(fields, options, config);
  parserCache.set(key, parser);
  return parser;
}

function setMaxCache(max) {
  parserCache.max = max;
}

function clearCache() {
  parserCache.reset();
}

module.exports = {
  getParser: getParser,
  setMaxCache: setMaxCache,
  clearCache: clearCache
};
