import React from 'react';
import { Container, Row, Col, Modal, Button } from 'react-bootstrap';
import './components.css';

let notes = [
  {
    id: 1,
    title: 'note 1',
    text: 'Lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white',
  },
  {
    id: 2,
    title: 'note 2',
    text: 'Lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white',
  },
  {
    id: 3,
    title: 'note 3',
    text: 'Lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white',
  },
  {
    id: 4,
    title: 'note 4',
    text: 'Lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white',
  },
  {
    id: 5,
    title: 'note 5',
    text: 'Lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white',
  }
];

class Notes extends React.Component {
  constructor(props) {
    super(props);
    this.textInput = React.createRef();
    this.state = {
      showModal: false,
      selectedNote: {},
    }
  }

  componentDidUpdate() {
    this.autoGrowTextArea();
  }

  handleCloseModal = () => {
    this.setState({ showModal: false })
  }

  onClickNote = (note) => {
    this.setState({ selectedNote: note, showModal: true });
  }

  changeTitle = (e) => {
    console.log(e.target.value);
  }

  autoGrowTextArea = () => {
    let element = this.textInput.current;
    element.style.height = '5px';
    element.style.height = (element.scrollHeight)+'px';
  }

  render() {
    return (
      <Container>
        <Row className='notes-row'>
          {
            notes.map(note => {
              return <Col sm={3} className='note-col' onClick={() => this.onClickNote(note)} key={note.id}>
                <p className='note-title'>{note.title}</p>
                <div>{note.text}</div>
              </Col>;
            })
          }
        </Row>
        <Modal
          show={this.state.showModal}
          onHide={this.handleCloseModal}
          backdrop='static'
          keyboard={false}
        >
          <Modal.Header closeButton>
            <Modal.Title>
              <textarea
                onChange={this.changeTitle}
                className='modal-title-textarea'
                defaultValue={this.state.selectedNote.title}
                maxLength='25'
              >
              </textarea>
            </Modal.Title>
          </Modal.Header>
          <Modal.Body className='modal-body'>
            <textarea
              ref={this.textInput}
              onChange={this.autoGrowTextArea}
              className='modal-text-textarea'
              defaultValue={this.state.selectedNote.text}
            >
            </textarea>
          </Modal.Body>
          <Modal.Footer>
            <Button variant='secondary' onClick={this.handleCloseModal}>Close</Button>
            <Button variant='primary'>Save</Button>
          </Modal.Footer>
        </Modal>
      </Container>
    );
  }
}

export default Notes;
