      title: 'note 3',
      text: 'Lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white',
    },
    {
      id: 4,
      title: 'note 4',
      text: 'Lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white',
    },
    {
      id: 5,
      title: 'note 5',
      text: 'Lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white',
    }
  ],
};

function reducer(state = initialState, action) {
  const { type, payload } = action;
  state = { ...state };
  switch(type) {
    case 'page-changed':
      state.page = payload.value;
      break;
    case 'notes-updated':
      state.notes = payload.notes;
      break;
    case 'create-note':
      state.notes.unshift(payload.note);
      break;
    default:
      break;
  }
  return state;
}

export default reducer;
