import React from 'react';
import { Container, Row, Col, Modal, Button } from 'react-bootstrap';
import './components.css';

let notes = [
  {
    title: 'note 1',
    text: 'Lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white',
  },
  {
    title: 'note 2',
    text: 'Lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white',
  },
  {
    title: 'note 3',
    text: 'Lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white',
  },
  {
    title: 'note 4',
    text: 'Lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white',
  },
  {
    title: 'note 5',
    text: 'Lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white lorem ipsum dolor white',
  }
];

class Notes extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showModal: false,
      noteTitle: ''
    }
  }

  handleCloseModal = () => {
    this.setState({ showModal: false })
  }

  render() {
    return (
      <Container>
        <Row className='notes-row'>
          {
            notes.map(note => {
              return <Col sm={3} className='note-col'>
                <p className='note-title'>{note.title}</p>
                <div>{note.text}</div>
              </Col>;
            })
          }
        </Row>
        <Modal
          show={this.state.showModal}
          onHide={this.handleCloseModal}
          backdrop='static'
          keyboard={false}
        >
          <Modal.Header closeButton>
            <Modal.Title>{this.state.noteTitle}</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            I will not close if you click outside me. Don't even try to press
            escape key.
          </Modal.Body>
          <Modal.Footer>
            <Button variant='secondary' onClick={handleClose}>
              Close
            </Button>
            <Button variant='primary'>Save</Button>
          </Modal.Footer>
        </Modal>
      </Container>
    );
  }
}

export default Notes;
